import React from 'react';
import styled from '@emotion/styled';

const NoteContainer = styled.div`
  padding: 15px;
  border: 1px solid #ccc;
  border-radius: 5px;
  margin-bottom: 10px;
  position: relative;
  cursor: pointer;
  background-color: #f8f8f8;

  h2 {
    margin: 0;
  }

  p {
    margin: 5px 0 0;
  }
`;

const DeleteButton = styled.button`
  position: absolute;
  top: 5px;
  right: 5px;
  background-color: #e74c3c;
  color: #fff;
  border: none;
  border-radius: 5px;
  padding: 5px 8px;
  font-size: 12px;
  cursor: pointer;
  opacity: 0;
  transition: opacity 0.2s ease-in-out;

  &:hover {
    background-color: #c0392b;
  }
`;

const Note = ({ note, onClick, onDelete }) => {
  const handleClick = (e) => {
    if (e.target !== e.currentTarget) return;
    onClick(note);
  };

  const handleDelete = (e) => {
    e.stopPropagation();
    onDelete(note.id);
  };

  return (
    <NoteContainer onClick={handleClick}>
      <h2>{note.title}</h2>
      <p>{note.content}</p>
      <DeleteButton onClick={handleDelete}>Delete</DeleteButton>
    </NoteContainer>
  );
};

export default Note;
