import React from 'react';
import styled from '@emotion/styled';

const DeleteButton = styled.button`
  background-color: #e74c3c;
  color: #fff;
  border: none;
  border-radius: 5px;
  padding: 5px 10px;
  font-size: 12px;
  cursor: pointer;
  float: right;
  margin-top: -5px;

  &:hover {
    background-color: #c0392b;
  }
`;

const Note = ({ note, onClick, onDelete }) => {
  const handleClick = (e) => {
    if (e.target !== e.currentTarget) return;
    onClick(note);
  };

  const handleDelete = (e, noteId) => {
    e.stopPropagation();
    onDelete(noteId);
  };

  return (
    <div onClick={handleClick}>
      <h3>{note.title}</h3>
      <DeleteButton onClick={(e) => handleDelete(e, note.id)}>Delete</DeleteButton>
      <p>{note.content}</p>
    </div>
  );
};

export default Note;
