import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch, Link } from 'react-router-dom';
import NoteList from './components/NoteList';
import NoteEditor from './components/NoteEditor';
import HomePage from './components/HomePage';

function App() {
  const [notes, setNotes] = useState([]);
  const [selectedNote, setSelectedNote] = useState(null);

  const handleNoteClick = (note) => {
    setSelectedNote(note);
  };

  const handleNewNoteClick = () => {
    setSelectedNote({ title: '', content: '' });
  };

  const handleNoteSave = (note) => {
    if (selectedNote) {
      setNotes(notes.map((n) => (n.id === note.id ? note : n)));
    } else {
      setNotes([...notes, { ...note, id: Date.now() }]);
    }
    setSelectedNote(null);
  };

  const handleNoteCancel = () => {
    setSelectedNote(null);
  };

  const handleNoteDelete = (id) => {
    setNotes(notes.filter((note) => note.id !== id));
    if (selectedNote && selectedNote.id === id) {
      setSelectedNote(null);
    }
  };

  return (
    <Router>
      <div>
        <nav>
          <ul>
            <li>
              <Link to="/">Home</Link>
            </li>
            <li>
              <Link to="/notes">Notes</Link>
            </li>
          </ul>
        </nav>

        <Switch>
          <Route path="/" exact>
            <HomePage onNewNoteClick={handleNewNoteClick} />
          </Route>
          <Route path="/notes">
            <h1>My Notes</h1>
            <NoteList notes={notes} onNoteClick={handleNoteClick} onDelete={handleNoteDelete} />
            {selectedNote ? (
              <NoteEditor note={selectedNote} onSave={handleNoteSave} onCancel={handleNoteCancel} />
            ) : (
              <NoteEditor note={{ title: '', content: '' }} onSave={handleNoteSave} onCancel={handleNoteCancel} />
            )}
          </Route>
        </Switch>
      </div>
    </Router>
  );
}

export default App;
