import React, { useState } from 'react';
import styled from '@emotion/styled';

const NoteContainer = styled.div`
  // ...
`;

const DeleteButton = styled.button`
  position: absolute;
  top: 5px;
  right: 5px;
  background-color: #e74c3c;
  color: #fff;
  border: none;
  border-radius: 5px;
  padding: 5px 8px;
  font-size: 12px;
  cursor: pointer;
  opacity: 0;
  transition: opacity 0.2s ease-in-out;

  &:hover {
    background-color: #c0392b;
  }
`;

const Note = ({ note, onClick, onDelete }) => {
  const [deleteVisible, setDeleteVisible] = useState(false);

  const handleClick = (e) => {
    if (e.target !== e.currentTarget) return;
    onClick(note);
  };

  const handleDelete = (e) => {
    e.stopPropagation();
    onDelete(note.id);
  };

  const showDelete = () => {
    setDeleteVisible(true);
  };

  const hideDelete = () => {
    setDeleteVisible(false);
  };

  return (
    <NoteContainer onClick={handleClick} onMouseEnter={showDelete} onMouseLeave={hideDelete}>
      <h2>{note.title}</h2>
      <p>{note.content}</p>
      <DeleteButton onClick={handleDelete} style={{ opacity: deleteVisible ? 1 : 0 }}>
        Delete
      </DeleteButton>
    </NoteContainer>
  );
};

export default Note;
